import Cabecera from "./components/Cabecera";
import Listado from "./components/Listado";
import {useState} from 'react';

function App() {
  const[itemsToComprar, setItemsToComprar] = useState(0);
  const onAdditemToComprar=() =>{
    setItemsToComprar(anterior => anterior+1)
  }

  return (
    <div className="App">
      <Cabecera itemsToComprar={itemsToComprar} />
      <Listado onAdditem={addItemToComprar}/>


    </div>
  );
}

export default App;
